/**
 * Options available for configuring a {@link Space}.
 */
export interface SpaceOptions {
    /**
     * Disable ABR publishing which means the participant will only publish 1 rendition of their video to the server.
     * This will lower CPU and bandwidth usage but can lead to poor performance in Spaces with more than 10 people.
     */
    disableABRPublishing?: boolean;
}
